package com.example.pillservice.controller.hc;

import com.ps.core.model.entity.PsUser;
import com.ps.core.model.web.drugs.DrugWeb;
import com.ps.core.model.web.drugs.DrugsListWeb;
import com.ps.core.model.web.drugs.PillWeb;
import com.ps.core.model.web.drugs.PillsListWeb;
import org.springframework.security.core.annotation.AuthenticationPrincipal;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.bind.annotation.RestController;

import java.util.ArrayList;
import java.util.List;

@RestController
@RequestMapping(value = "/en/users")
public class PillsController {

    @GetMapping(value = "/pills")
    public PillsListWeb getPills(@AuthenticationPrincipal PsUser user,
                                 @RequestParam(value = "page") Integer page,
                                 @RequestParam(value = "size") Integer size,
                                 @RequestParam(value = "hc") Integer hc) {

        List<PillWeb> drugs = new ArrayList<>();
        drugs.add(PillWeb.builder()
                .id("1")
                .title("ACAMOL")
                .price("3.00")
                .quantityPerItem("2")
                .discount("2.00")
                .image(getImage())
                .build());

        return PillsListWeb.builder()
                .drugs(drugs)
                .build();
    }

    @GetMapping(value = "/recipe-pills")
    public DrugsListWeb getPillsByRecipe(@AuthenticationPrincipal PsUser user,
                                         @RequestParam(value = "page") Integer page,
                                         @RequestParam(value = "size") Integer size) {

        List<DrugWeb> drugs = new ArrayList<>();
        drugs.add(DrugWeb.builder()
                .id("2")
                .title("TRAMADOL")
                .price("3.00")
                .quantityPerItem("2")
                .discount("2.00")
                .requiresRecipe("true")
                .build());

        return DrugsListWeb.builder()
                .drugs(drugs)
                .build();
    }

    private String getImage() {
        return "data:image/jpeg;base64,";
    }
}
